// vector data processor
const geo = {
	init() {
		// symbols, mohr, th1n, deco, shape
		// geo.a = geo.proc(th1n);
		// geo.b = geo.line();
	},
	proc(data) {
		// threejs vectors
		let a = {};
		// keys
		let b = [];
		for (const k0 in data) {
			a[k0] = geo.vec(data[k0]);
			b.push(k0);
		}
		return [a,b];
	},
	vec(d0) {
		let r0 = [];
		for (let i = 0; i < d0.length; i++) {
			// triple depth line segments points array
			let d1 = d0[i];
			let d2 = d1[0];
			let d3 = d1[1];
			let x0 = d2[0];
			let y0 = 1024 - d2[1];
			let x1 = d3[0];
			let y1 = 1024 - d3[1];
			let v0 = new THREE.Vector3(x0,y0,0);
			let v1 = new THREE.Vector3(x1,y1,0);
			r0.push([v0,v1]);
		}
		return r0;
	},
	line() {
		let b = {};
		// material
		b.a = {};
		// geometry
		b.b = {};
		// line
		b.c = {};
		for (const k0 in geo.a[0]) {
			b.a[k0] = [];
			b.b[k0] = [];
			b.c[k0] = [];
			let p0 = geo.a[0][k0];
			let r0 = Math.random();
			let g0 = Math.random();
			let b0 = Math.random();
			let c0 = new THREE.Color(r0,g0,b0);	
			for (let i = 0; i < p0.length; i++) {
				// let r0 = Math.random();
				// let g0 = Math.random();
				// let b0 = Math.random();
				// let c0 = new THREE.Color(r0,g0,b0);
				b.a[k0][i] = new THREE.LineBasicMaterial({color:c0});
				// b.a[k0][i] = new THREE.LineBasicMaterial({color:0x00ff00});
				b.b[k0][i] = new THREE.BufferGeometry().setFromPoints(p0[i]);
				b.c[k0][i] = new THREE.Line(b.b[k0][i], b.a[k0][i]);
			}
		}
		return b;
	},
	rand() {
		let f0 = (Math.random() * geo.a[1].length) >>> 0;
		let k0 = geo.a[1][f0];
		return k0;
	}
};

// angle
const rad = {
	polar(x,y) {
		// cartesian to polar transform
		let r0 = [];
		let r1 = Math.sqrt(x*x + y*y);
		let th0 = Math.atan2(y,x);
		r0[0] = r1;
		r0[1] = th0;

		return r0;
	},
	cart(r0,th0) {
		// input polar, output carto
		let r1 = [];
		let x0 = Math.cos(th0);
		x0 = x0 * r0;
		let y0 = Math.sin(th0);
		y0 = y0 * r0;
		r1[0] = x0;
		r1[1] = y0;

		return r1;
	}
};

// bitmap
const bit = {
	init() {
		// names list
		bit.a = ["th1n",
			"symbol",
			"alchemy",
			"mohr"
		];
		// class list
		bit.b = [th1n,
			symbol,
			alchemy,
			mohr
		];
		// keys
		bit.c = [];
		for (let i = 0; i < bit.a.length; i++) {
			let k0 = bit.a[i];
			bit.c[i] = [];
			let c0 = bit.b[i];
			// console.log(c0.constructor.name);
			// refactor as lists with set index
			for (const k1 in c0) {
				bit.c[i].push(k1);
			}
		}
		console.log(bit.c);
		// data 
		bit.d = [];
	},
	proc() {
		// threejs vector3 line segments
		// loop over class
		// inner loop over bitmap
		for (let i = 0; i < bit.b.length; i++) {
			let c0 = bit.b[i];
			let k0 = bit.c[i];
			for (let j = 0; j < k0.length; j++) {
				// method to get points array, triple depth
				bit.d[i][j] = bit.vec(c0[k0]);
			}
		}
	},
	vec(d0) {
		let r0 = [];
		for (let i = 0; i < d0.length; i++) {
			// triple depth line segments points array
			let d1 = d0[i];
			let d2 = d1[0];
			let d3 = d1[1];
			let x0 = d2[0];
			let y0 = 1024 - d2[1];
			let x1 = d3[0];
			let y1 = 1024 - d3[1];
			let v0 = new THREE.Vector3(x0,y0,0);
			let v1 = new THREE.Vector3(x1,y1,0);
			r0.push([v0,v1]);
		}
		return r0;
	},
	line(k0,k1) {
		// input class, key ("th1n","A")
		// putput three js group for line segments
		let i0 = bit.a.indexOf(k0);
		let j0 = bit.c[i0].indexOf(k1);
		let d0 = bit.d[i0][j0];
		// three js line object
		let g0 = new THREE.Group();
		for (let i = 0; i < d0.length; i++) {
			let mat0 = new THREE.LineBasicMaterial({color:0x00ff00});
			let buf0 = new THREE.BufferGeometry().setFromPoints(d0[i]);
			let line0 = new THREE.Line(buf0, mat0);
			g0.add(line0);
		}
		return g0;
	}
};

